function numberChecker(number) {
 

  if (number === 1000) {
    return true;
  } else {
    return false;
  }

  if (number === 10) {
    return true;
  } else {
    return false;
  }

  if (number === 9) {
    return true;
  } else {
    return false;
  }
 
  if (number === 6) {
    return true;
  } else {
    return false;
  }
}

// Do not edit below this line
module.exports = numberChecker;
